package helloworld;

public class evenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int no=12;
		if (no>   0) {
			if(no%2==0) {
				System.out.println("even number");
			}
			else {
				System.out.println("odd number");
			}
			
		}
		else {
			System.out.println("number smaller than  zero");
		}
		
          
	}

}
